import React from 'react'
import { Form } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

function ModalPopUp(props) {
    const colors = ['#ff0000', '#00ff00', '#0000ff', '#ffff00', '#ff00ff', '#00ffff'];
    const [groupName, setGroupName] = useState('');

    const handleGroupNameChange = (event) => {
        setGroupName(event.target.value);
    };

    const handleCreateClick = () => {
        props.hideModal();
        // Pass the group name to the parent component
        props.onGroupCreated(groupName);
    };
    return (
        <>
            <Modal
                {...props}
                size="md"
                aria-labelledby="contained-modal-title-vcenter"
                centered
            >
                <div className='text-center mt-4'>
                    <h3>Create New Notes Group</h3>
                </div>
                <Modal.Body >
                    <Form>
                        <div className='container text-start'>
                            <div className='row g-3'>
                                <div className='col-6'>
                                    <h5>Group Name</h5>
                                </div>
                                <div className='col-6'>
                                    <Form.Control
                                        type="text"
                                        placeholder="Your note name"
                                        autoFocus
                                        value={groupName}
                                        onChange={handleGroupNameChange}
                                    />
                                </div>
                                <div className='col-6'>
                                    <h5>Choose Color</h5>
                                </div>
                                <div className='col-6'>
                                    <div className="color-picker-container">
                                        {colors.map((showColor, index) => (
                                            <div key={index} className="color-circle" style={{ backgroundColor: showColor }}></div>
                                        ))}
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className='text-center my-3'>
                            <Button onClick={handleCreateClick} className='main-create-btn'>Create</Button>
                        </div>
                    </Form>
                </Modal.Body>
            </Modal>
        </>
    )
}

export default ModalPopUp
